package myPackage;

/**
 * @author user
 *
 */
public class ForLoop {
	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for (int i = 1; i <= 10; i++){
			int doubled = i * 2;
			System.out.println(i + " times two equals " + doubled);
		}
		System.out.println("End of program");
	}
}
