package myPackage;

/**
 * @author user
 *
 */
public class WhileLoop {
	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i = 1;
		while(i <= 10){
			int doubled = i * 2;
			System.out.println(i + " times two equals " + doubled);
			i++;
		}
		System.out.println("End of program");
	}
}
